import{_ as e,c as i,a2 as t,o as l}from"./chunks/framework.D6PIRKQ_.js";const r="/assets/%E4%B8%8A%E4%BC%A0%E5%9C%B0%E5%9D%80.vzliK0OK.png",x=JSON.parse('{"title":"地址","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"mailing/dizhi.md","filePath":"mailing/dizhi.md"}'),o={name:"mailing/dizhi.md"};function n(s,a,d,h,p,c){return l(),i("div",null,a[0]||(a[0]=[t('<h1 id="地址" tabindex="-1">地址 <a class="header-anchor" href="#地址" aria-label="Permalink to &quot;地址&quot;">​</a></h1><h2 id="地址规范" tabindex="-1">地址规范 <a class="header-anchor" href="#地址规范" aria-label="Permalink to &quot;地址规范&quot;">​</a></h2><p>能够妥投的地址信息应当包含以下5个部分：</p><p>姓名 州/省/郡 城市/县/区 邮政编码 街道地址（地址1/地址2/地址3…） 对于法国、意大利、西班牙、英国、德国、奥地利、荷兰、波兰的地址，可能会出现部分地址没有“州”的信息。</p><p>基于每个国家的地址格式有不少差异的原因，我们提供了完善的地址验证功能，将为您尽最大可能规避因地址文件的错误而产生的损失，优化邮件的交付能力。</p><h2 id="导入地址" tabindex="-1">导入地址 <a class="header-anchor" href="#导入地址" aria-label="Permalink to &quot;导入地址&quot;">​</a></h2><p><img src="'+r+'" alt="上传地址.png"></p><h2 id="地址验证" tabindex="-1">地址验证 <a class="header-anchor" href="#地址验证" aria-label="Permalink to &quot;地址验证&quot;">​</a></h2><p>为什么要地址验证？</p><blockquote><p>地址是否重复、是否完整、是否能够投递、是否是风险地址等因素会带来成本和安全上的问题。通过地址验证，会过滤掉不合规的地址，一方面减低成本，另一方面尽可能降低风险。</p></blockquote><p>地址验证时机：</p><ul><li>订单提交并且状态进入处理中后会自动进行前期校验，并且只提交验证成功地址；</li><li>订单提交状态进入制作中邮寄前验证是否能投递；</li></ul><h2 id="数量" tabindex="-1">数量 <a class="header-anchor" href="#数量" aria-label="Permalink to &quot;数量&quot;">​</a></h2><p>订单列表数量栏会显示每个订单地址数量，数量会根据不通状态展示不通的格式，以下是不通格式对应的含义：</p><ul><li>格式：xx 订单创建时引用地址模板的有效地址总数，简称“总数”</li><li>格式：xx/xx 订单地址检验后实际提交至印刷的数量/总数，简称“提交数量/总数”</li><li>格式：xx/xx/xx 订单实际印刷并寄出的数量/提交数量/总数，简称“成功数量/提交数量/总数”</li></ul>',15)]))}const m=e(o,[["render",n]]);export{x as __pageData,m as default};
